	public void SetEnemyText (int indice, string nameObj, string txt) {
		GameObject cacheListEnemy = enemyEmpty.transform.Find (string.Format ("Enemy ({0})", indice)).gameObject;

		GameObject cacheTextEnemy = cacheListEnemy.transform.Find (nameObj).gameObject;

		cacheTextEnemy.gameObject.GetComponent<TextMesh> ().text = txt;
	}

	public string GetEnemyText (int indice, string nameObj) {
		GameObject cacheListEnemy = enemyEmpty.transform.Find (string.Format ("Enemy ({0})", indice)).gameObject;

		GameObject cacheTextEnemy = cacheListEnemy.transform.Find (nameObj).gameObject;

		return cacheTextEnemy.gameObject.GetComponent<TextMesh> ().text.ToString ();
	}

	private string Capitalize (string str) {
		if (string.IsNullOrEmpty (str))
			return string.Empty;

		return char.ToUpper (str[0]) + str.Substring (1);
	}

	private void RandomWord (int max) {
		indiceWord = Random.Range (0, max);

		wordRandomEn = Capitalize (words[indiceWord].en);

		wordRandomPt = Capitalize (words[indiceWord].pt.Split (',') [0]);
	}

	public void SetAllWords () {
		// Debug.Log ("Set All Words");

		for (int c = 0; c < quantityEnemys; c++) {
			RandomWord (maxWords);

			SetEnemyText (c, "Text Enemy", wordRandomEn + " | " + wordRandomPt);
		}
	}

